            '\n  @charset "UTF-8";\n/*\n==========================================================================\nCitizens NS\n==========================================================================\n*/\n.citizens-header-footer .topshadow {\n  display: none;\n}\n\n.citizens-header-footer .footer-disclaimer {\n  border: 3px solid #808080;\n  font-weight: bold;\n  color: #666;\n  margin-bottom: 15px;\n  padding: 10px 100px;\n  text-align: center;\n}\n\n.citizens-header-footer .footer-disclaimer .footer-disclaimer-box__text {\n  font-family: CitiSans, arial;\n  font-size: 16px;\n  color: #666;\n  margin: 0;\n}\n\n.citizens-header-footer .footer-disclaimer .footer-disclaimer-box__list {\n  display: block;\n  margin: 0;\n  padding: 0;\n}\n\n.citizens-header-footer .footer-disclaimer .footer-disclaimer-box__list-item {\n  font-family: CitiSans, arial;\n  font-size: 16px;\n  list-style: none;\n  display: inline;\n  padding-left: 3px;\n  text-transform: uppercase;\n}\n\n.citizens-header-footer .footer-disclaimer-box__list-item::before {\n  content: "";\n  font-size: 21px;\n  line-height: 12px;\n  padding-right: 3px;\n}\n\n.responsive-enabled .page-logo img {\n  max-width: none;\n}\n\n@media screen and (max-width: 880px) {\n  .responsive-enabled .two-col .main-content {\n    float: left;\n  }\n}\n\n.account-section-title.checkmark h1 {\n  padding: 0px 0px 5px 28px !important;\n}\n\n.timeout-modal {\n  font-size: 1em !important;\n  line-height: 1.4 !important;\n  font-family: CitiSans, Helvetica, Arial, sans-serif !important;\n  color: #444;\n}\n\n.footer-row {\n  line-height: 1.4;\n  font-family: CitiSans, Helvetica, Arial, sans-serif;\n}\n\n.citone-footer-row ul {\n  width: 44%;\n}\n\n.citone-footer-row ul.last {\n  width: 12%;\n}\n\n.footer-row a {\n  font-size: 12px;\n}\n\n.citizens-header-footer .error-message {\n  position: relative;\n  background: #fef3f3;\n  color: red;\n  padding: 0px;\n}\n\n.citizens-header-footer div {\n  font-family: CitiSans, Helvetica, Arial, sans-serif;\n}\n\n.timeout-modal p {\n  font-size: 1em !important;\n  line-height: 1.4 !important;\n  font-family: "CitiSans", Helvetica, Arial, sans-serif !important;\n}\n\n.timeout-modal .button {\n  font-size: 0.8em !important;\n  font-weight: 600 !important;\n  line-height: 22px !important;\n}\n\n/* To fix the checkbox in IE browsers */\ninput[type="checkbox"] {\n  visibility: hidden;\n  position: absolute;\n}\n\ninput[type="radio"] {\n  visibility: hidden;\n  position: absolute;\n}\n\nselect#SavedUserID {\n  max-width: 249px;\n}\n\n/* Fix iOS divices to restrict ZOOM in */\n.form-item input[type="text"] {\n  font-size: 16px;\n}\n\n.form-item input[type="password"] {\n  font-size: 16px;\n}\n\n.error-message h1 {\n  color: #f00;\n}\n\n.status-message h1 {\n  color: #209db9;\n}\n\n.warning-message h1 {\n  color: #ee9037;\n}\n\nselect#SavedUserID,\ninput#Response,\ninput#currentpassword,\ninput#UserID {\n  height: 32px;\n}\n\n#stickyFooterBranch {\n  color: #444;\n}\n\ninput.submit-button.arrow[disabled],\n.button.arrow[disabled] {\n  background: url(/images/arrow-right-gray.png) right center no-repeat #bcbebf;\n  background-position-x: 55px;\n}\n\n/* IE fix for the footer to not show the underline on image*/\na.contact,\na.locator {\n  text-decoration: none;\n}\n\n.account-underline:hover {\n  text-decoration: underline;\n}\n\n#confirm-password-tooltip.tooltip-right .tooltip-box {\n  left: 40px;\n}\n\n/*show/hide password for login pages*/\n/*#showpswd {\n    cursor: pointer;\n    color: #808080;\n    position:absolute;\n    bottom: 15px;\n}\n.show-hide {\n    display: inline-block;\n    margin-left: -60px;\n    padding-right: 20px;\n    margin-right: 35px;\n}*/\n@media screen and (max-width: 900px) {\n  select#SavedUserID {\n    max-width: 100%;\n  }\n}\n\n.enrollment-error-msg {\n  color: red;\n  font-size: 16px;\n  padding: 20px 20px 20px 50px;\n  background: url(/images/global-error.png) 15px no-repeat;\n}\n\n/*IE fix for not to display eye or x icon in password box*/\n/*input[type=password]::-ms-clear, input[type=password]::-ms-reveal, input[type=text]::-ms-clear{\n    display:none;\n}\ninput[type=password]::-ms-clear , input[type=text]::-ms-clear {\n    width : 0;\n    height: 0;\n}*/\n/*login- faq issue fix*/\n.account-section .loginfaq .faq-content-list {\n  padding-left: 15px;\n}\n\n.account-section .loginfaq .faq-content-list li {\n  list-style-type: disc;\n}\n\n/*#enrollshowpswd {\n    cursor: pointer;\n    color: gray;\n    position: absolute;\n    bottom: 7px;\n}*/\n/* Added CSS for onfocus of check box element image visibility.As per ALM#3073*/\n#cbSaveUserID {\n  visibility: visible;\n}\n\n.save-userid {\n  font-family: "Arial", sans-serif;\n}\n\nlabel[for="currentpassword"],\nlabel[for="UserID"],\nlabel[for="cbSaveUserID"] {\n  font-family: "Arial", sans-serif;\n  font-size: 14px;\n}\n\n.wao-cancel {\n  padding-left: 15px;\n  color: #f1912b;\n  text-transform: uppercase;\n  font-size: 12px;\n  cursor: pointer;\n}\n\n.rememberme-label {\n  padding-top: 25px;\n}\n\n/*Fix font for all input*/\ninput[type="text"] {\n  font-family: Arial, sans-serif;\n}\n\n/*@media screen and (max-width: 880px) {\n    .show-hide {\n        margin-right: 0px;\n    }\n}*/\n.tooltip-right-custom {\n  float: right;\n}\n\n/* safari fix for show password*/\n/*.citizens-Safari .show-hide {\n    margin-left: -75px;\n    margin-right: 51px;\n}*/\n@media screen and (max-width: 880px) {\n  .tooltip-box {\n    left: 0px;\n  }\n}\n\n@media screen and (min-width: 881px) {\n  .tooltip-box {\n    left: 40px;\n  }\n}\n\ninput#stickyFooterBranch {\n  font-family: "CitiSans", "Trebuchet MS", Helvetica, Arial, sans-serif;\n  width: 166px;\n}\n\n/*\nSRT-1180 to pack the global message.\n */\n#GlobalMessageContainer .global-message {\n  margin: 0px;\n  padding: 10px 20px 10px 50px;\n}\n\n#GlobalMessageContainer .global-message p {\n  margin: 0.5em 0;\n}\n\n#GlobalMessageContainer .global-message h1 {\n  font-size: 20px;\n}\n\n@media screen and (max-width: 460px) {\n  .responsive-enabled .timeout-modal {\n    width: auto;\n  }\n  .simplemodal-wrap {\n    height: auto !important;\n  }\n}\n\n@media screen and (max-width: 600px) {\n  .citizens-header-footer .footer-disclaimer {\n    padding: 10px 40px;\n  }\n}\n\n/*# sourceMappingURL=citizensns.min.44438.css.map */\n\n  ',
